    public static double transformECG(int n, int ADC){
        double ECG_V, ECG_mV;
        int G_ECG, VCC;

        VCC = 3;      // operating voltage
        G_ECG = 1000; // sensor gain

        ECG_V = (ADC/Math.pow(2, n) - 0.5)*VCC/G_ECG;

        ECG_mV = ECG_V*1000;

        return ECG_mV;
    }

